import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import SwapMultipleTokens from "./artifacts/contracts/SwapMultipleTokens.sol/SwapMultipleTokens.json";

const swapContractAddress = "0x35523D7aC91c1290B6E7a2f35F18Ad5d1A4EF96a";
const WETH_ADDRESS = "0x7ceB23fD6bC0adD59E62ac25578270cFf1b9f619"; // WETH address on Polygon
const USDC_ADDRESS = "0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174"; // USDC address on Polygon

function Swap() {
  const [maticAmount, setMaticAmount] = useState("");
  const [wethAmount, setWethAmount] = useState("");
  const [amountOutMin, setAmountOutMin] = useState("");
  const [recipient, setRecipient] = useState("");
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);

  useEffect(() => {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      setProvider(provider);
    }
  }, []);

  const connectWallet = async () => {
    if (!window.ethereum) return alert("Please install MetaMask");
    try {
      const accounts = await provider.send("eth_requestAccounts", []);
      setAccount(accounts[0]);
      const signer = provider.getSigner();
      setSigner(signer);
    } catch (error) {
      console.error("Failed to connect wallet:", error);
    }
  };

  const handleSwap = async () => {
    if (!signer) return alert("Please connect your wallet first");

    const swapContract = new ethers.Contract(
      swapContractAddress,
      SwapMultipleTokens.abi,
      signer
    );
    const wethContract = new ethers.Contract(
      WETH_ADDRESS,
      [
        "function approve(address spender, uint256 amount) public returns (bool)",
      ],
      signer
    );

    try {
      const maticValue = ethers.utils.parseEther(maticAmount); // MATIC is the native currency
      const wethValue = ethers.utils.parseUnits(wethAmount, 18);
      const minOut = ethers.utils.parseUnits(amountOutMin, 6); // USDC typically has 6 decimals
      const deadline = Math.floor(Date.now() / 1000) + 900; // 15 minutes from now

      // Approve WETH transfer
      const approveTx = await wethContract.approve(
        swapContractAddress,
        wethValue
      );
      await approveTx.wait();

      // Swap transaction
      const tx = await swapContract.swapMultipleTokens(
        USDC_ADDRESS, // USDC address
        maticValue,
        wethValue,
        minOut,
        recipient,
        deadline,
        { value: maticValue }
      );
      await tx.wait();
      alert("Swap successful");
    } catch (error) {
      console.error("Swap failed:", error);
      alert(`Swap failed: ${error.message}`);
    }
  };

  return (
    <div>
      <h1>Swap MATIC and WETH to USDC</h1>
      <button onClick={connectWallet}>
        {account ? `Connected: ${account}` : "Connect Wallet"}
      </button>
      <div>
        <label>MATIC Amount:</label>
        <input
          type="text"
          value={maticAmount}
          onChange={(e) => setMaticAmount(e.target.value)}
        />
      </div>
      <div>
        <label>WETH Amount:</label>
        <input
          type="text"
          value={wethAmount}
          onChange={(e) => setWethAmount(e.target.value)}
        />
      </div>
      <div>
        <label>Minimum USDC Amount Out:</label>
        <input
          type="text"
          value={amountOutMin}
          onChange={(e) => setAmountOutMin(e.target.value)}
        />
      </div>
      <div>
        <label>Recipient Address:</label>
        <input
          type="text"
          value={recipient}
          onChange={(e) => setRecipient(e.target.value)}
        />
      </div>
      <button onClick={handleSwap}>Swap</button>
    </div>
  );
}

export default Swap;
